template.value:
AnimatedBuilder(
  animation: $animationController$,
  child: $childWidget$,
  builder: (BuildContext context, Widget child) {
    return $buildWidget$;
  },
);

showDialog<void>(
context: $context$,
barrierDismissible: $dismiss$, // false = user must tap button, true = tap outside dialog
builder: (BuildContext dialogContext){
return AlertDialog(
title: Text('$title$'),
content: Text('$content$'),
actions: <Widget>[
FlatButton(
child: Text('$buttonText$'),
onPressed: () {
Navigator.of(dialogContext).pop(); // Dismiss alert dialog
},
),
],
);
},
);

int _index = 0; // Make sure this is outside build(), otherwise every setState will chage the value back to 0 

return Scaffold(
appBar: AppBar(
title: Text('$appbarTitle$'),
),
body: Center(
child: Container(child: Text('You are looking at the message for bottom navigation item $_index')),
),
bottomNavigationBar: BottomNavigationBar(
onTap: (tappedItemIndex)=> setState(() {
_index = tappedItemIndex;
}),
currentIndex: _index,
items: [
BottomNavigationBarItem(icon: Icon(Icons.av_timer), title: Text('$navBarItem1Text$')),
BottomNavigationBarItem(icon: Icon(Icons.add), title: Text('$navBarItem2Text$'))
]),
floatingActionButton: FloatingActionButton(
onPressed: () => setState(() {
=_index = 0;
}),
tooltip: 'Reset Navigation Index',
        child: Icon(Icons.clear), //Change Icon
),
floatingActionButtonLocation: FloatingActionButtonLocation.endFloat, //Change for different locations
);

return Scaffold(
appBar: AppBar(
title: Text('$appbarTitle$'),
),
body: $body$
);

int _index = 0; // Make sure this is outside build(), otherwise every setState will chage the value back to 0 

